import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';  // Import useHistory

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);  // Add loading state
  const [errorMessage, setErrorMessage] = useState('');  // Add error message state
  const navigate = useNavigate();  // Initialize useHistory

  const handleSubmit = async (e) => {
    e.preventDefault();  // Prevent form from reloading the page

    setLoading(true);
    setErrorMessage('');  // Clear any previous error messages

    try {
      // Send POST request to the backend login API
      const response = await axios.post('http://localhost:5000/api/login', {
        email,
        password,
      });

      // If login is successful, store the JWT token
      const { token } = response.data;
      localStorage.setItem('token', token);

      // Redirect to the home/dashboard page after successful login
      navigate.push('/home');  // This will take the user to the home page

      setLoading(false);
    } catch (error) {
      setLoading(false);

      // Handle error (Invalid credentials, server error, etc.)
      if (error.response) {
        setErrorMessage(error.response.data.message || 'Login failed');
      } else {
        setErrorMessage('An error occurred. Please try again later.');
      }
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
          {errorMessage && <p className="error">{errorMessage}</p>}
        </form>
      </div>
    </div>
  );
};

export default Login;
