import React, { useState } from 'react';
import { Grid,Paper, TextField, Typography,Button } from '@mui/material';

const Register = () =>{
  const heading={fontSize:"2.5em",fontweight:"600"};
  const paperStyle={padding:"2rem",margin:"100px auto",borderRadius:"1rem",boxShadow:"10px 10px 10px"};
  const row={display:"flex",marginTop:"2rem"};
  const btnStyle={marginTop:"2rem",fontSize:"1.2rem",fontWeight:"700",backgroundColor:"blue",borderRadius:"0.5rem"};
  
  const[name,setName]=useState("");
  const[email,setEmail]=useState("");
  const[password,setPassword]=useState("");
  return(
    <div>
    <Grid align="center">
<Paper style={paperStyle} sx={{ width:{
xs:"80vw",
sm:"50vw",
md:"40vw",
lg:"30vw",
xl:"20vw"
},
height:'60vh'}}>
  <Typography style={heading}>
    Register
  </Typography>
  <form onSubmit>
    <TextField onChange={(e)=>setName(e.target.value)}name="name" required sx={{label:{fontWeight:'700',fontSize:'1.3rem'}}}  style ={row} label="Enter Name" type="text"></TextField>
    <TextField onChange={(e)=>setEmail(e.target.value)}name="email"required  sx={{label:{fontWeight:'700',fontSize:'1.3rem'}}} style ={row} label="EnterEmail" type='email'></TextField>
    <TextField onChange={(e)=>setPassword(e.target.value)}name="password"required  sx={{label:{fontWeight:'700',fontSize:'1.3rem'}}} style ={row} label="Enter Password"type='password'></TextField>
    <Button type="submit" variant="contained" style={btnStyle}>Register</Button>
  </form>

</Paper>
    </Grid>
    </div>
  );

  

};
export default Register;
