import React, { useState } from "react";
import Modal from "react-modal";

const BookCard = ({ book }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [quantity, setQuantity] = useState(1); // Default quantity is 1

  const openModal = () => setModalIsOpen(true);
  const closeModal = () => setModalIsOpen(false);

  // Increase or decrease quantity
  const changeQuantity = (amount) => {
    if (quantity + amount > 0) {
      setQuantity(quantity + amount);
    }
  };

  // Handle Add to Cart
  const handleAddToCart = () => {
    const cartItem = { ...book, quantity };
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];

    savedCart.push(cartItem);
    localStorage.setItem("cart", JSON.stringify(savedCart));

    console.log(`Added ${quantity} of "${book.title}" to the cart.`);
    closeModal();
  };

  return (
    <div className="book-card">
      <img src={book.image} alt={book.title} onClick={openModal} />
      <h3>{book.title}</h3>
      <p>Author: {book.author}</p>
      <p>Price: ₹{book.price}</p>

      {/* Modal to show book details */}
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} contentLabel="Book Details">
        <h2>{book.title}</h2>
        <p>{book.description}</p>

        {/* Quantity Selector */}
        <div className="quantity-selector">
          <button onClick={() => changeQuantity(-1)} disabled={quantity <= 1}>-</button>
          <span>{quantity}</span>
          <button onClick={() => changeQuantity(1)}>+</button>
        </div>

        <div className="modal-footer">
          <p>Total Price: ₹{book.price * quantity}</p>
          <button onClick={closeModal}>Close</button>
          <button onClick={handleAddToCart}>Add to Cart</button>
        </div>
      </Modal>
    </div>
  );
};

export default BookCard;
