import React, { useState } from "react";
import Modal from "react-modal";
import axios from "axios";
const BookCard = ({ book }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [quantity, setQuantity] = useState(1); // Default quantity is 1

  const openModal = () => setModalIsOpen(true);
  const closeModal = () => setModalIsOpen(false);

  // Increase or decrease the quantity
  const changeQuantity = (amount) => {
    if (quantity + amount > 0 && quantity + amount <= book.quantity) {
      setQuantity(quantity + amount);
    }
  };

  // Handle Add to Cart
  const handleAddToCart = async () => { 
    const cartItem = { ...book, quantity };
    const savedCart = JSON.parse(localStorage.getItem("cart")) || [];

    savedCart.push(cartItem);

    localStorage.setItem("cart", JSON.stringify(savedCart));

    console.log(`Added ${quantity} of "${book.title}" to the cart.`);
    try {
      await axios.post('http://localhost:3001/update-book-quantity', { 
        bookId: book._id, 
        quantity 
      });
  
      
      setQuantity(book.quantity - quantity);
  
    } catch (error) {
      console.error('Error updating book quantity:', error);
    }
    closeModal();
  };

  return (
    <div className="book-card" onClick={openModal}>
      <img src={book.image} alt={book.title} />
      <h3>{book.title}</h3>
      <p>Author: {book.author}</p>
      <p>Price: ₹{book.price * quantity}</p>
      <p>Available: {book.quantity}</p>

      <div className="quantity-selector">
        <button onClick={() => changeQuantity(-1)} disabled={quantity <= 1}>
          -
        </button>
        <span>{quantity}</span>
        <button
          onClick={() => changeQuantity(1)}
          disabled={quantity >= book.quantity}
        >
          +
        </button>
      </div>

      {/* Modal to show book details */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Book Details"
      >
        <h2>{book.title}</h2>
        <p>{book.description}</p>
        <div className="modal-footer">
          <p>Price: ₹{book.price * quantity}</p>
          <button onClick={closeModal}>Close</button>
          <button onClick={handleAddToCart}>Add to Cart</button>
        </div>
      </Modal>
    </div>
  );
};

export default BookCard;
