import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "./App.css";
import Navbar from "./components/navbar";        // Navbar for the first page
import Register from "./components/Register";
import Login from "./components/Login";
import Navbarc from "./components/navbarc";      // Navbar for Login and Register
import Logout from "./components/logout";
import HomePagenavbar from "./components/HomePagenavbar"; // Navbar after login
import Home from "./components/Home";            // Home page after login

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if user is logged in (you can check for a token or any other method)
    if (localStorage.getItem("authToken")) {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <Router>
      {/* Render Navbar for the first page */}
      {!isLoggedIn && window.location.pathname === "/" && <Navbar />}

      {/* Render Navbarc for Login and Register */}
      {(window.location.pathname === "/login" || window.location.pathname === "/register") && <Navbarc />}

      {/* Render HomePagenavbar if logged in */}
      {isLoggedIn && window.location.pathname !== "/login" && window.location.pathname !== "/register" && <HomePagenavbar />}

      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/logout" element={<Logout />} />

        {/* Redirect to Home page after login */}
        <Route path="/home" element={isLoggedIn ? <Home /> : <Navigate to="/login" />} />

        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
