import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "./App.css";
import Navbar from "./components/navbar";        // For the main page navbar
import Home from "./components/Home";
import Register from "./components/Register";
import Login from "./components/Login";
import Navbarc from "./components/navbarc";      // Navbar for login/register
import Logout from "./components/logout";
import HomePageNavbar from "./components/Homepage Navbar";    // Your main page component

const App = () => {
  // State to track if user is logged in (you can enhance this logic with a more secure method)
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {

    if (localStorage.getItem("authToken")) {
      setIsLoggedIn(true);
    }
  }, []);

  return (
    <Router>

      {(window.location.pathname === "/login" || window.location.pathname === "/register") ? <Navbarc /> : null}


      {isLoggedIn && (window.location.pathname !== "/login" && window.location.pathname !== "/register") && <HomePageNavbar />}

      <Routes>

        <Route path="/" element={<Navbar/>} />
        <Route path="/home" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/logout" element={<Logout />} />


        {isLoggedIn && <Route path="/main" element={<HomePageNavbar />} />}

        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
