import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "./App.css";
import Navbar from "./components/navbar";        // Navbar for the first page
import Register from "./components/Register";
import Login from "./components/Login";
import Navbarc from "./components/navbarc";      // Navbar for Login and Register
import Logout from "./components/logout";
import HomePageNavbar from "./components/HomePageNavbar"; // Navbar after login
import Home from "./components/Home";            // Home page after login

const App = () => {
  // State to track login status
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    // Set the logged-in state to true (in a real app, you'd check credentials here)
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // Set the logged-out state and redirect to the login page
    setIsLoggedIn(false);
  };

  return (
    <Router>
      {/* Render Navbar for the first page */}
      {!isLoggedIn && window.location.pathname === "/" && <Navbar />}

      {/* Render Navbarc for Login and Register pages */}
      {(window.location.pathname === "/login" || window.location.pathname === "/register") && !isLoggedIn && <Navbarc />}

      {/* Render HomePagenavbar if logged in */}
      {isLoggedIn && window.location.pathname !== "/login" && window.location.pathname !== "/register" && <HomePageavbar />}

      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/logout" element={<Logout onLogout={handleLogout} />} />

        {/* Protected Route */}
        <Route path="/home" element={isLoggedIn ? <Home /> : <Navigate to="/login" />} />

        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
};

export default App;
