import React, { useState } from 'react';
import axios from 'axios';  // Ensure axios is imported

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);  // Add loading state
  const [errorMessage, setErrorMessage] = useState('');  // Add error message state

  // Make handleSubmit async to use await inside
  const handleSubmit = async (e) => {
    e.preventDefault();  // Prevent form from reloading the page

    setLoading(true);
    setErrorMessage('');  // Clear any previous error messages

    try {
      // Send POST request to the backend login API
      const response = await axios.post('http://localhost:5000/api/login', {
        email,
        password,
      });

      // If login is successful, store the JWT token (you can store it in localStorage or a context)
      const { token } = response.data;

      // Save the token (in localStorage or a context)
      localStorage.setItem('token', token);

      // Redirect to home page or dashboard after successful login
      // For example: window.location.href = '/home';

      alert('Login successful!');
      setLoading(false);
    } catch (error) {
      setLoading(false);

      // Handle error (Invalid credentials, server error, etc.)
      if (error.response) {
        // Server responded with a status other than 2xx
        setErrorMessage(error.response.data.message || 'Login failed');
      } else {
        // Network or other error
        setErrorMessage('An error occurred. Please try again later.');
      }
    }
  };

  return (
    <div className="auth-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Enter your password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
        {errorMessage && <p className="error">{errorMessage}</p>}  {/* Show error message if any */}
      </form>
    </div>
  );
};

export default Login;
