import React from 'react';
import { Grid, Paper, TextField } from '@mui/material';

export default function Register() {
  return (
    <Grid 
      container 
      justifyContent="center" 
      alignItems="center" 
      style={{ height: '100vh' }} 
    >
      <Grid item>
        <Paper 
          sx={{
            width: {
              xs: '80vw', // For small screens
              sm: '50vw', // For medium screens
              md: '40vw', // For larger screens
              lg: '30vw', // For even larger screens
              xl: '20vw', // For extra-large screens
            },
            height: '70vh', // Adjusted height
            padding: 3, // Added padding to prevent content from touching edges
          }}
        >
          <form>
            <TextField 
              fullWidth 
              label="Enter Name" 
              variant="outlined" 
              sx={{ marginBottom: 2 }} 
            />
            <TextField 
              fullWidth 
              label="Enter Email" 
              variant="outlined" 
              sx={{ marginBottom: 2 }} 
            />
            <TextField 
              fullWidth 
              label="Enter Password" 
              type="password" 
              variant="outlined" 
              sx={{ marginBottom: 2 }} 
            />
          </form>
        </Paper>
      </Grid>
    </Grid>
  );
}
