import React,{useState} from 'react'
import { Grid,Paper, TextField, Typography,Button } from '@mui/material';



export default function Login() {
  const heading={fontSize:"2.5em",fontweight:"800"};
    const paperStyle={padding:"2rem",margin:"100px auto",borderRadius:"1rem",boxShadow:"10px 10px 10px"};
    const row={display:"flex",marginTop:"2rem"};
    const btnStyle={marginTop:"4rem",fontSize:"1.4rem",fontWeight:"700",backgroundColor:"blue",borderRadius:"0.5rem"};
    const[email,setEmail]=useState();
    const[password,setPassword]=useState();

    return(
      <div>
      <Grid align="center">
  <Paper style={paperStyle} sx={{ width:{
  xs:"80vw",
  sm:"50vw",
  md:"40vw",
  lg:"30vw",
  xl:"20vw"
  },
  height:'56vh'}}>
    <Typography style={heading}>
      Login
    </Typography>
    <form on>
      <TextField onChange={(e)=>setEmail(e.target.value)} name="email" sx={{label:{fontWeight:'700',fontSize:'1.3rem'}}} style ={row} label="Enter Email" type='email'></TextField>
      <TextField onChange={(e)=>setEmail(e.target.value)}  name="password" sx={{label:{fontWeight:'700',fontSize:'1.3rem'}}} style ={row} label="Enter Password"type='password'></TextField>
      <Button type="submit" variant="contained" style={btnStyle}>Login</Button>
    </form>
  
  </Paper>
      </Grid>
      </div>
    );
  
    
  
  };
