import React from 'react';
import { Grid,Paper, TextField, Typography,Button } from '@mui/material';

const Register = () =>{
  const heading={fontSize:"2.5em",fontweight:"600"};
  const paperStyle={padding:"2rem",margin:"100px auto",borderRadius:"1rem",boxShadow:"10px 10px 10px"};
  const row={display:"flex",marginTop:"2rem"};
  const btnStyle={marginTop:"2rem",fontSize:"1.2rem"}
  return(
    <div>
    <Grid align="center">
<Paper style={paperStyle} sx={{ width:{
xs:"80vw",
sm:"50vw",
md:"40vw",
lg:"30vw",
xl:"20vw"
},
height:'60vh'}}>
  <Typography style={heading}>
    Register
  </Typography>
  <form>
    <TextField  style ={row} label="Enter Name"></TextField>
    <TextField style ={row} label="EnterEmail"></TextField>
    <TextField style ={row} label="Enter Password"></TextField>
    <Button style={btnStyle}></Button>
  </form>

</Paper>
    </Grid>
    </div>
  );

  

};
export default Register;
